import './Homepage.scss';
import { Grid, Typography, Button } from '@mui/material';
import { useEffect, useState } from 'react';
import { aget } from '../../net/Axios';
import { Link } from 'react-router-dom';

// Define the paths to the banner images
const bannerImages = [
  '/src/components/asset/banner1.png',
  '/src/components/asset/banner2.png',
  '/src/components/asset/banner3.png',
];

export default function Homepage() {
  const [products, setProducts] = useState([]);
  const [currentBanner, setCurrentBanner] = useState(0);

  useEffect(() => {
    aget('/products').then((res) => {
      setProducts(res.data);
    });

    const bannerInterval = setInterval(() => {
      setCurrentBanner((prevBanner) => (prevBanner + 1) % bannerImages.length);
    }, 5000); // Change banner every 5 seconds

    return () => clearInterval(bannerInterval); // Clean up interval on component unmount
  }, []);

  return (
    <div className='homepage'>
      <div className='banner-container'>
        <div 
          className='banner' 
          style={{ backgroundImage: `url(${bannerImages[currentBanner]})` }}
        ></div>
      </div>
      <div>
        <p className='title'>Our products</p>
        <p className='subtitle'>Explore some of our favorite jewelries</p>
        <div className='products-container'>
          <Grid container spacing={2}>
            {products.map((product) => (
              <Grid item xs={12} sm={3} md={3} key={product.id}>
                <div className='card'>
                  <div 
                    className='image' 
                    style={{ backgroundImage: `url(${product.image})` }}
                  ></div>
                  <p className='title'>{product.name}</p>
                  <Button 
                    variant="contained" 
                    className='btn-explore' 
                    component={Link} 
                    to={`/products/${product.id}`}
                  >
                    Explore
                  </Button>
                </div>
              </Grid>
            ))}
          </Grid>
        </div>
      </div>
    </div>
  );
}
