import './Homepage.scss';
import { Grid, Typography, Button } from '@mui/material';
import { useEffect, useState } from 'react';
import { aget } from '../../net/Axios';
import { Link } from 'react-router-dom';
import Banner from './Banner';

export default function Homepage() {
  let [products, setProducts] = useState([]);

  useEffect(() => {
    aget('/products').then((res) => {
      setProducts(res.data);
    });
  }, []);

  return (
    <div className='homepage'>
      <div className='banner-container'>
        <Banner />
      </div>
      <div>
        <Typography variant="h4" className='title'>Our products</Typography>
        <Typography variant="subtitle1" className='subtitle'>Explore some of our favorite jewelries</Typography>
        <div className='products-container'>
          <Grid container spacing={2}>
            {products.map((product) => (
              <Grid item xs={12} sm={6} md={3} key={product.id}>
                <div className='card'>
                  <div className='image' style={{ backgroundImage: `url(${product.image})` }}></div>
                  <Typography variant="h6" className='title'>{product.name}</Typography>
                  <Button variant="contained" className='btn-explore' component={Link} to={`/products/${product.id}`}>Explore</Button>
                </div>
              </Grid>
            ))}
          </Grid>
        </div>
      </div>
    </div>
  );
}
